package tp9;

public class LimiteDeByte {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		LimiteByte();

	}
	public static void LimiteByte() { //fonction qui sert à trouver la limite d'un byte
		byte x = 0 ;
		byte X = 1 ;
		while(x < X) { 
			
			x++;
			X++;
		}System.out.println("La valeur maximale d'un  byte est "+x+".");
	}

}
